package com.example.zhouganglibrary.views;import android.content.Context;import android.support.v4.view.ViewPager;import android.util.AttributeSet;import android.view.MotionEvent;public class CustomViewPager extends ViewPager {	public static boolean isCanScroll = true;		public CustomViewPager(Context context) {		super(context);	}	public CustomViewPager(Context context, AttributeSet attrs) {		super(context, attrs);	}	@Override	public boolean dispatchTouchEvent(MotionEvent ev) {		getParent().requestDisallowInterceptTouchEvent(true);			return super.dispatchTouchEvent(ev);	}		@Override	public void scrollTo(int x, int y) {		super.scrollTo(x, y);	}		@Override	public boolean onTouchEvent(MotionEvent arg0) {		if (!isCanScroll)			return false;		else			return super.onTouchEvent(arg0);	}	@Override	public boolean onInterceptTouchEvent(MotionEvent arg0) {		if (!isCanScroll)			return false;		else			return super.onInterceptTouchEvent(arg0);	}	@Override	public void setCurrentItem(int item, boolean smoothScroll) {		super.setCurrentItem(item, smoothScroll);	}	@Override	public void setCurrentItem(int item) {		super.setCurrentItem(item);	}}